import { GET_PLAYERS, ADD_PLAYER } from '../actions/types.js';

const initialState = {
  players: [{
       id: 0,
       name: "",
       multiplier: 0,
       dares: []
     }]
}

export default function(state = initialState, action) {
  switch(action.type) {
    case GET_PLAYERS:
      return {
        ...state
      };
      default:
        return state;
  }
}
