//react libraries
import React, { Component } from "react";
import PropTypes from 'prop-types';
import { Button, Modal, ModalHeader, ModalBody, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

//redux
import { connect } from 'react-redux'
import { editDare } from '../../actions/dareActions'

class EditDareModal extends Component{
    constructor(props){
        super(props);

        this.state = {
            text: "",
            nsfw: false,
            chalengable: false,
            pointValue: 1
        }
    }

    static getDerivedStateFromProps(nextProps, prevState) {
        return {
            text: props.dare.text
        };
    }

    hangleChange(event){
        this.setState({
            text: event.target.value
        });
    }

    render(){
        return(
            <Modal
                isOpen={this.props.dares.modal}
                toggle={this.props.editDare}
            >
                <ModalHeader toggle={this.props.editDare}>
                    Edit dare
                </ModalHeader>
                <ModalBody>
                    <Form onSubmit={this.onSubmit}>
                        <FormGroup>
                            <Label for="dare">Dare text</Label>
                            <Input 
                                type="text"
                                name="text"
                                id="dare"
                                value={this.state.text}
                                onChange={this.handleChange}
                            />
                            <Button
                                color="light"
                                style={{marginTop: '2rem'}}
                                block
                            >
                                Update
                            </Button>
                        </FormGroup>
                    </Form>
                </ModalBody>
            </Modal>

        );
    }

}

EditDareModal.propTypes = {
    editDare: PropTypes.func.isRequired
}

const mapStateToProps = (state) => ({
    dares: state.dares
});

export default connect(mapStateToProps, { editDare })(EditDareModal);
