import axios from 'axios';
import {
  ADD_DARE,
  GET_DARES,
  DARES_LOADING
} from './types';

export const addDare = text => {
  return {
    type: ADD_DARE,
    payload: text
  };
};

export const getDares = () => dispatch => {
  dispatch(setItemsLoading());
  axios
    .get('/api/dares')
};

export const setDaresLoading = () => {
  return {
    type: DARES_LOADING
  };
};
