//react libraries
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import '../../index.css';
import PropTypes from 'prop-types';

//redux
import { connect } from 'react-redux'
import { deleteDare, editDare } from '../../actions/dareActions'

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

//font awesome icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

//componenets
import EditDareModal from './edit-dare-modal';

class EditDares extends Component{
  render(){
    let modal = <EditDareModal dare={this.props.dare.editDare}/>

    return(
      <div>
        {this.props.dares.loading ?
          <div>loading</div> :
          <div>
            {modal}
            {this.props.dares.filteredDares.map(dare => (
                <div class="row shadow panel" key={dare._id}>
                  <div class="col-10 offset-1">
                    <p>
                      {dare.text}
                    </p>
                  </div>
                  <div class="col">
                    <button onClick={() => this.props.editDare(dare)}>
                      <FontAwesomeIcon icon="edit"/>
                    </button>
                    <button onClick={() => this.props.deleteDare(dare._id)}>
                      <FontAwesomeIcon icon="trash-alt"/>
                    </button>
                  </div>
                </div>
            ))}
          </div>
        }
      </div>
    );
  }
}

EditDares.propTypes = {
  deleteDare: PropTypes.func.isRequired,
  editDare: PropTypes.func.isRequired
}

const mapStateToProps = (state) => ({
  dares: state.dares
});

export default connect(mapStateToProps, { deleteDare, editDare })(EditDares);
