import { TOGGLE, ADD_PLAYER } from '../actions/types.js';

const initialState = {
  warningIsActive: false
}

export default function(state = initialState, action) {
  switch(action.type) {
    case TOGGLE:
      return {
        ...state,
        warningIsActive: !state.warningIsActive
      };
    case ADD_PLAYER:
      return {
        ...state,
        players: [player, ...state.players]
      };
    default:
      return state;
  }
}
