//react libraries
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import './../index.css';
import PropTypes from 'prop-types';

//redux
import { connect } from 'react-redux'
import { darePlayers } from '../actions/controlsActions'

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

//font awesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

class DarePlayers extends Component{
  constructor(props){
    super(props);

  }

  darePlayers(){
    for(var i = 0; i<this.props.player.players.length; i++){
      let dareIndex = Math.floor(Math.random()*this.props.dares.dares.length);
  
      this.props.darePlayer(this.props.player.players[i].id, this.props.dares.dares[dareIndex]);
    }
  }
  
  render(){
    return(
      <button title="Dare all" class="hamburger" onClick={this.props.darePlayers}>
        <FontAwesomeIcon icon="dice" size="2x" />
      </button>
    );
  }
}

DarePlayers.propTypes = {
  darePlayers: PropTypes.func.isRequired,
  darePlayers: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
  player: state.player
});

export default connect(mapStateToProps, { darePlayer })(DarePlayers);
