function translate(promptText){
  var dares = [];

  //console.log(promptText);

  while(promptText.length > 0){
    //find start of dare
    var dareTextStart = promptText.indexOf("The Dare:");

    //find end of dare
    var dareTextEnd = promptText.indexOf("Point Value:");

    //extract dare
    var extractedText = promptText.slice(dareTextStart+10, dareTextEnd);

    //extract point Value
    var extractedPointValue = promptText.slice(dareTextEnd+13, dareTextEnd+14);

    //extract challengeable
    var extractedChallengeable = promptText.slice(dareTextEnd+32, dareTextEnd+34);

    if(extractedChallengeable == "Ye"){
      extractedChallengeable = promptText.slice(dareTextEnd+32, dareTextEnd+35);
      //cut string
      promptText = promptText.slice(dareTextEnd+35);
    } else {
      //cut string
      promptText = promptText.slice(dareTextEnd+34);
    }

    //new object
    dares.push(
      {
        text: extractedText,
        pointValue: extractedPointValue,
        challengeable: extractedChallengeable
      }
    );

    if(dares.length > 2){
      break;
    }
  }
  
  for(i = 0; i<dares.length; i++){
    console.log(dares[i].text);
    console.log(dares[i].pointValue);
    console.log(dares[i].challengeable);
  }

}

export default translate;
