//react libraries
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import '../../index.css';
import PropTypes from 'prop-types';

//redux
import { connect } from 'react-redux'
import { deleteDare, editDare } from '../../actions/dareActions'

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

//font awesome icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

//componenets
import EditDareModal from './edit-dare-modal';

class EditDares extends Component{
  constructor(props){
    super(props);

    this.state = {
      modalIsOpen: false,
      openDare: {}
    }

    this.openModal = this.openModal.bind(this);
  }

  openModal(dare){
    console.log("open modal");

    this.setState({
      modalIsOpen: !this.state.modalIsOpen,
      openDare: dare
    });
  }

  render(){
    return(
      <div>
        {this.props.dares.loading ?
          <div>loading</div> :
          <div class="container-fluid" >
            <div class="row">
              <div class="col">
                <EditDareModal
                  dare={{text: "fuck"}}
                />
              </div>
            </div>
            {this.props.dares.filteredDares.map(dare => (
                <div class="row shadow panel" key={dare._id}>
                  <div class="col-10 offset-1">
                    <p>
                      {dare.text}
                    </p>
                  </div>
                  <div class="col">
                    <button onClick={() => this.props.editDare(dare)}>
                      <FontAwesomeIcon icon="edit"/>
                    </button>
                    <button onClick={() => this.props.deleteDare(dare._id)}>
                      <FontAwesomeIcon icon="trash-alt"/>
                    </button>
                  </div>
                </div>
            ))}
          </div>
        }
      </div>
    );
  }
}

EditDares.propTypes = {
  deleteDare: PropTypes.func.isRequired,
  editDare: PropTypes.func.isRequired
}

const mapStateToProps = (state) => ({
  dares: state.dares
});

export default connect(mapStateToProps, { deleteDare, editDare })(EditDares);
