//react libraries
import React, { Component } from "react";
import PropTypes from 'prop-types';
import { Button, Modal, ModalHeader, ModalBody, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

//redux
import { connect } from 'react-redux'
import { editDare } from '../../actions/dareActions'

class EditDareModal extends Component{
    constructor(props){
        super(props);

        this.state = {
            dare: {}
        }

        this.hangleChange = this.hangleChange.bind(this);
    }

    hangleChange(event){
        this.setState({
            dare: {...this.state.dare, text: event.target.value}
        });
    }

    render(){
        return(
            <Modal
                isOpen={this.props.isOpen}
                toggle={() => this.props.toggle(this.props.dare)}
            >
                <ModalHeader toggle={this.props.toggle}>
                    Edit dare
                </ModalHeader>
                <ModalBody>
                    <Form onSubmit={this.onSubmit}>
                        <FormGroup>
                            <Label for="dare">Dare text</Label>
                            <Input 
                                type="text"
                                name="text"
                                id="dare"
                                value={this.state.dare.text}
                                onChange={this.handleChange}
                            />
                            <Button
                                color="light"
                                style={{marginTop: '2rem'}}
                                block
                            >
                                Update
                            </Button>
                        </FormGroup>
                    </Form>
                </ModalBody>
            </Modal>

        );
    }

}

export default EditDareModal;
