function translate(text){
  while(text.length > 0){
    //find start of dare
    var dareTextStart = text.indexOf("The Dare:");

    //find end of dare
    var dareTextEnd = text.indexOf("Point Value:");

    //extract dare
    var extractedText = text.slice(dareTextStart+10, dareTextEnd);

    //extract point Value
    var pointValue = text.slice(dareTextEnd+13, dareTextEnd+14);

    //extract challengeable
    var challengeable = text.slice(dareTextEnd+29, dareTextEnd+31);

    if(challengeable == "ye"){
      challengeable = text.slice(dareTextEnd+29, dareTextEnd+32);
      //cut string
      text = text.slice(dareTextEnd+32);
    } else {
      //cut string
      text = text.slice(dareTextEnd+31);
    }

    break;
  }
  console.log(text);
}

export default translate;
