//react libraries
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import './../index.css';

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

//font awesome icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

//components
import AddPlayer from "../game/add-player.jsx";
import DarePlayers from "../game/dare-players.jsx";
import ClearDares from "../game/clear-dares.jsx";
import DeletePlayers from "../game/delete-players.jsx";

class Controls extends Component{
  constructor(props){
    super(props);

    this.state = {
      warningIsActive: false
    };
  }

  render(){
    return(
      <div class="container">
        <div class="row">
          <div class="col">
            <AddPlayer onClick={this.props.addPlayer}/>
          </div>
        </div>
        <div class="row">
          <div class="col">
            <DarePlayers onClick={this.darePlayers} />
          </div>
        </div>
        <div class="row">
          <div class="col">
            <ClearDares onClick={this.clearDares} />
          </div>
        </div>
        <div class="row">
          <div class="col">
            <DeletePlayers onClick={this.deletePLayer} />
          </div>
        </div>
        <div class="row">
          <div class="col">
            <Settings onClick={() => this.go("settings")} />
          </div>
        </div>
        <div class="row">
          <div class="col">
            {this.state.warningIsActive ?
              <div class="hamburger alert alert-warning">
                You are about to remove all players.
              </div> : ""
            }
          </div>
        </div>
      </div>
    );
  }
}

export default Controls;
