//react libraries
import React, { Component } from "react";
import { connect } from "mongoose";
import { Button, Modal, ModalHeader, ModalBody, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

class EditDareModal extends Component{
    constructor(props){
        super(props);

        this.state = {
            text: this.props.dare.text,
            nsfw: false,
            chalengable: false,
            pointValue: 1
        }
    }

    hangleChange(event){
        this.setState({
            text: event.target.value
        });
    }

    render(){
        return(
            <Modal
                isOpen={this.props.isOpen}
                toggle={this.props.toggle}
            >
                <ModalHeader toggle={this.props.toggle}>
                    Edit dare
                </ModalHeader>
                <ModalBody>
                    <Form onSubmit={this.onSubmit}>
                        <FormGroup>
                            <Label for="dare">Dare text</Label>
                            <Input 
                                type="text"
                                name="text"
                                id="dare"
                                value={this.state.dare.text}
                                onChange={this.handleChange}
                            />
                            <Button
                                color="light"
                                style={{marginTop: '2rem'}}
                                block
                            >
                                Update
                            </Button>
                        </FormGroup>
                    </Form>
                </ModalBody>
            </Modal>

        );
    }

}
  
const mapStateToProps = (state) => ({
dares: state.dares
});

export default connect(mapStateToProps)(EditDareModal);
