//react libraries
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import './../index.css';

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

//components
import Banner from "./../game/banner.jsx";
import Controls from "./../game/controls.jsx";
import Players from "./../player/players.jsx";
import AddPlayer from "./add-player.jsx";
import DarePlayers from "./dare-players.jsx";
import ResetDares from "./reset-players.jsx";
import DeletePlayers from "./delete-players.jsx";
import Settings from "./settings.jsx";

class Darelist extends Component {
  constructor(props){
    super(props);

    this.state = {
      players: [{
        id: 0,
        name: "",
        multiplier: 0,
        dares: []
      }],
      playerCounter: 1,
      dareCounter: 0
    }
  }

  render(){
    const newPlayer = {
      id: 0,
      name: "",
      multiplier: 0,
      dares: []
    }

    return(
      <div class="container-fluid" align="center">
        <div class="row" id="banner">
          <div class="col">
            <Banner />
          </div>
        </div>
        <div class="row">
          <div class="col-2">
            <Controls />
              <AddPlayer onClick={() => this.setState({players: this.state.players.concat(newPlayer)})}/>
              <DarePlayers />
              <ResetDares />
              <DeletePlayers />
              <Settings />
            <Controls />
          </div>
          <div class="col-8">
            <Players players={this.state.players}/>
          </div>
        </div>
      </div>
    );
  }
}

export default Darelist;
