function translate(text){
  /*
  var dare = text.slice(text.indexOf("The Dare: ");
  */

  while(text.length > 0){
    //find start of dare
    var dareTextStart = text.indexOf("The Dare:");

    //find end of dare
    var dareTextEnd = text.indexOf("Point Value:");

    //extract dare
    var extractedText = text.slice(dareTextStart+10, dareTextEnd);

    //extract point Value
    var pointValue = text.slice(dareTextEnd+13, dareTextEnd+14);

  }

  console.log(text);
}

export default translate;
