import { ADD_DARE } from '../actions/types.js';

const initialState = {
  dares: [],
  dareCounter: 0,
  loading: false
}

export default function(state = initialState, action) {
  switch(action.type) {
    case ADD_DARE:
      return {
        ...state,
        dares: [...state.dares, {text: action.payload, id: state.dareCounter}],
        dareCounter: state.dareCounter + 1
      };
    default:
      return state;
  }
}
