import { ADD_PLAYER, GET_PLAYERS, DELETE_PLAYERS, DARE_PLAYERS } from '../actions/types.js';

const player = {
  id: 0,
  name: "",
  multiplier: 0,
  dares: []
}

const initialState = {
  players: [ player ]
}

export default function(state = initialState, action) {
  switch(action.type) {
    case GET_PLAYERS:
      return {
        ...state
      };
    case DELETE_PLAYERS:
      return {
        ...state,
        players: []
      };
    case ADD_PLAYER:
      return {
        ...state,
        players: [...state.players, player]
      };
    case DARE_PLAYERS:
      return {
        players: state.players.map((player) => {
          player.dares = player.dares.concat({text: "new dare"});
          return player;
        })
      };
    default:
      return state;
  }
}
