//react libraries
import React, { Component } from "react";
import { connect } from "mongoose";
import { Modal, ModalHeader, ModalBody, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

class EditDareModal extends Component{
    constructor(props){
        super(props);

        this.state = {
            modal: false,
            text: '',
            nsfw: false,
            chalengable: false,
            pointValue: 1
        }
    }

    hangleChange(event){
        this.setState({
            text: event.target.value
        });
    }

    render(){
        return(
            <Modal
                isOpen={this.state.modal}
                toggle={this.props.toggle}
            >
                <ModalHeader toggle={this.props.toggle}>
                    Edit dare
                </ModalHeader>
                <ModalBody>
                    <Form onSubmit={this.onSubmit}>
                        <FormGroup>
                            <Label for="dare">Dare</Label>
                            <Input 
                                type="text"
                                name="text"
                                id="dare"
                                placeholder="passed dare text"
                                onChange={this.handleChange}
                            />
                        </FormGroup>
                    </Form>
                </ModalBody>
            </Modal>

        );
    }

}

export default EditDareModal;
