//react libraries
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import '../../index.css';
import PropTypes from 'prop-types';

//redux
import { connect } from 'react-redux'
import { deleteDare } from '../../actions/dareActions'

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

//font awesome icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

//componenets
import EditDareModal from './edit-dare-modal';

class EditDares extends Component{
  constructor(props){
    super(props);

    this.state = {
      modalIsOpen: false
    }

    this.toggle = this.toggle.bind(this);
  }

  toggle(){
    this.setState({
      modalIsOpen: true
    });
  }

  render(){
    return(
      <div>
        {this.props.dares.loading ?
          <div>loading</div> :
          <div class="container-fluid shadow panel" >
            <div class="row">
              <div class="col">
                <EditDareModal isOpen={this.state.modalIsOpen}/>
              </div>
            </div>
            {this.props.dares.filteredDares.map(dare => (
                <div class="row shadow panel" key={dare._id}>
                  <div class="col-10 offset-1">
                    <p>
                      {dare.text}
                    </p>
                  </div>
                  <div class="col">
                    <button>
                      <FontAwesomeIcon icon="edit"/>
                    </button>
                    <button onClick={() => this.props.deleteDare(dare._id)}>
                      <FontAwesomeIcon icon="trash-alt"/>
                    </button>
                  </div>
                </div>
            ))}
          </div>
        }
      </div>
    );
  }
}

EditDares.propTypes = {
  deleteDare: PropTypes.func.isRequired
}

const mapStateToProps = (state) => ({
  dares: state.dares
});

export default connect(mapStateToProps, { deleteDare })(EditDares);
