//react libraries
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import './../index.css';

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

//components
import Banner from "./../game/banner.jsx";
import Controls from "./../game/controls.jsx";
import Players from "./../player/players.jsx";
import AddPlayer from "./../game/add-player.jsx";
import DarePlayers from "./../game/dare-players.jsx";
import ResetDares from "./../game/reset-players.jsx";
import DeletePlayers from "./../game/delete-players.jsx";
import Settings from "./../game/settings.jsx";

class Darelist extends Component {
  constructor(props){
    super(props);

    this.state = {
      players: [{
        id: 0,
        name: "",
        multiplier: 0,
        dares: []
      }],
      playerCounter: 1,
      dareCounter: 0
    }

    this.addPlayer = this.addPlayer.bind(this);
  }

  addPlayer(){
    var players = this.state.players.concat(
      {
        id: 0,
        name: "",
        multiplier: 0,
        dares: []
      }
    );

    this.setState({
      players: players,
      playerCount: this.state.playerCount+1
    });
  }

  render(){
    return(
      <div class="container-fluid" align="center">
        <div class="row" id="banner">
          <div class="col">
            <Banner />
            <button onClick={() => console.log(this.state.players)}>
              DEBUG
            </button>
          </div>
        </div>
        <div class="row">
          <div class="col-2">
            <Controls>
              <AddPlayer onClick={this.addPlayer}/>
              <DarePlayers />
              <ResetDares />
              <DeletePlayers />
              <Settings />
            </Controls>
          </div>
          <div class="col-8">
            <Players players={this.state.players}/>
          </div>
        </div>
      </div>
    );
  }
}

export default Darelist;
