import axios from 'axios';
import {
  ADD_DARE,
  GET_DARES,
  DARES_LOADING,
  DELETE_DARE
} from './types';

export const addDare = text => {
  return {
    type: ADD_DARE,
    payload: text
  };
};

export const getDares = () => dispatch => {
  dispatch(setDaresLoading());
  axios
    .get('/api/dares')
    .then(res =>
      dispatch({
        type: GET_DARES,
        payload: res.data
      })
    )
};

export const setDaresLoading = () => {
  return {
    type: DARES_LOADING
  };
};

export const deleteDare = () => {
  return {
    type: DELETE_DARE
  };
};
