//react libraries
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import './../index.css';
import PropTypes from 'prop-types';

//redux
import { connect } from 'react-redux'

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

//font awesome icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

class EditDares extends Component{
  constructor(props){
    super(props);

    this.state = {
      value: ''
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleKeypress = this.handleKeypress.bind(this);
  }

  handleChange(event){
    this.setState({value: event.target.value});
  }

  handleSubmit(event){
    event.preventDefault();
  }

  handleKeypress(event){
    if(event.key === "Enter"){
      event.target.blur();
    }
  }

  render(){
    return(
      <div class="container shadow panel">
        <div class="row">
          <div class="col-11">
            <p>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus in ex vulputate, malesuada erat hendrerit, condimentum metus. Aenean vel tristique lorem. Ut sit amet orci ac mauris vulputate suscipit. Praesent massa lacus, mattis vel massa vitae, tristique luctus sem. Vestibulum pulvinar metus massa, ac ornare nibh dapibus et. Maecenas eget urna ut risus viverra efficitur nec sed magna. Vivamus condimentum velit ut turpis porta, in auctor lorem commodo. Etiam malesuada nisl sed aliquet faucibus. Nullam at elementum diam. Nunc sit amet leo ipsum. Orci varius natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus. Ut vel nisi lacus. Suspendisse et nisl lectus. Nulla eros tellus, semper id aliquam viverra, luctus id purus. Suspendisse ultricies urna quis ante feugiat, at pellentesque sapien aliquam.
            </p>
          </div>
          <div class="col">
            <FontAwesomeIcon icon="trash-alt"/>
            <FontAwesomeIcon icon="edit"/>
          </div>
        </div>
      </div>
    );
  }
}

export default EditDares;
